// load module
var imagecolors = require('imagecolors');

/**
 * extract predominant colors from image
 * note: maximum is currently capped at 96, need to do load testing before raising
 * usage: extract(imagePath, numColors, callback)
 */
imagecolors.extract('./octocat.png', 8, function(err, colors){
    if (!err){
        //console.log('EXTRACTED');
		//console.log(colors);
        for(var i = 0; i < 8; i++)
        {
             console.log("Color:" + i)
             console.log("Hex:" + colors[i].hex);
             console.log("Percent:" + colors[i].percent);
             console.log("Family:" +colors[i].family);
             console.log();
        }
        
       
        //console.log();
    }

});